import { formatTime, parseTime, formatTimeTwo } from '@/utils/index'
export default {
 props: {
    printList: {
      type: Array,
      required: true
    },
    toQuery: {
      type: Function,
      required: true
    }
  },
  data() {
    return {
      loading: false, dialog: false
    }
  },
  mounted() {
    window.doPrint = this.doPrint;
  },
  methods: {
    formatTime,
    parseTime,
    formatTimeTwo,
    cancel() {
      this.dialog = false
    },
    indexMethod(index){
      return index+1;
    },
    doPrint(){
      let printbox = document.querySelector("#printList").innerHTML;
      document.querySelector("body").innerHTML = printbox;
      window.print();
      this.cancel();
      // this.toQuery();
      window.location.reload();
    },
    getSummaries(param) {
      const { columns, data } = param;
      const sums = [];
      columns.forEach((column, index) => {
        if (index === 0) {
          sums[index] = '合计';
          return;
        }else if(index === 7){
          const values = data.map(item => Number(item.cartInfoMap.productInfo.price*item.cartInfoMap.cartNum));
          sums[index] = values.reduce((prev, curr) => {
            const value = Number(curr);
            if (!isNaN(value)) {
              return prev + curr;
            } else {
              return prev;
            }
          }, 0);
          sums[index] += ' 元';
        }
      });
      return sums;
    }
  }
}
