import checkPermission from '@/utils/permission'
import initData from '@/mixins/crud'
import { del } from '@/api/yxStoreCoupon'
import eForm from './form'
import eIForm from '../couponissue/form'
import { formatTime } from '@/utils/index'
export default {
  components: { eForm, eIForm },
  mixins: [initData],
  data() {
    return {
      delLoading: false
    }
  },
  created() {
    this.$nextTick(() => {
      this.init()
    })
  },
  methods: {
    formatTime,
    checkPermission,
    beforeInit() {
      this.url = 'api/yxStoreCoupon'
      const sort = 'id,desc'
      this.params = { page: this.page, size: this.size, sort: sort, isDel: 0 }
      return true
    },
    subDelete(id) {
      this.delLoading = true
      del(id).then(res => {
        this.delLoading = false
        this.$refs[id].doClose()
        this.dleChangePage()
        this.init()
        this.$notify({
          title: '删除成功',
          type: 'success',
          duration: 2500
        })
      }).catch(err => {
        this.delLoading = false
        this.$refs[id].doClose()
        console.log(err.response.data.message)
      })
    },
    add() {
      this.isAdd = true
      this.$refs.form.dialog = true
    },
    edit(data) {
      this.isAdd = false
      const _this = this.$refs.form
      _this.form = {
        id: data.id,
        title: data.title,
        integral: data.integral,
        couponPrice: data.couponPrice,
        useMinPrice: data.useMinPrice,
        couponTime: data.couponTime,
        sort: data.sort,
        status: data.status,
        addTime: data.addTime,
        isDel: data.isDel
      }
      _this.dialog = true
    },
    edit2(data) {
      this.isAdd = true
      const _this = this.$refs.form2
      _this.form = {
        cid: data.id,
        cname: data.title,
        isPermanent: 0,
        status: 1,
        totalCount: 0,
        remainCount: 0,
        isDel: 0
      }
      _this.dialog = true
    }
  }
}
