import { add, edit } from '@/api/yxSystemUserLevel'
import picUpload from '@/components/pic-upload'
import MaterialList from '@/components/material'
export default {
  components: { picUpload, MaterialList },
  props: {
    isAdd: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      loading: false, dialog: false,
      form: {
        id: '',
        merId: '',
        name: '',
        money: '',
        validDate: '',
        isForever: 1,
        isPay: '',
        isShow: 1,
        grade: '',
        discount: '',
        image: '',
        icon: '',
        imageArr: [],
        iconArr: [],
        explain: '',
        addTime: '',
        isDel: ''
      },
      rules: {
      }
    }
  },
  watch: {
    'form.imageArr': function(val) {
      if (val) {
        this.form.image = val.join(',')
      }
    },
    'form.iconArr': function(val) {
      if (val) {
        this.form.icon = val.join(',')
      }
    }
  },
  methods: {
    cancel() {
      this.resetForm()
    },
    doSubmit() {
      this.loading = true
      if (this.isAdd) {
        this.doAdd()
      } else this.doEdit()
    },
    doAdd() {
      add(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '添加成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    doEdit() {
      edit(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '修改成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    resetForm() {
      this.dialog = false
      this.$refs['form'].resetFields()
      this.form = {
        id: '',
        merId: '',
        name: '',
        money: '',
        validDate: '',
        isForever: 1,
        isPay: '',
        isShow: 1,
        grade: '',
        discount: '',
        image: '',
        icon: '',
        imageArr: [],
        iconArr: [],
        explain: '',
        addTime: '',
        isDel: ''
      }
    }
  }
}
