import checkPermission from '@/utils/permission'
import initData from '@/mixins/crud'
import { del } from '@/api/yxStoreBargain'
import eForm from './form'
import { formatTimeTwo, parseTime } from '@/utils/index'
export default {
  components: { eForm },
  mixins: [initData],
  data() {
    return {
      delLoading: false
    }
  },
  created() {
    this.$nextTick(() => {
      this.init()
    })
  },
  methods: {
    parseTime,
    formatTimeTwo,
    checkPermission,
    beforeInit() {
      this.url = 'api/yxStoreBargain'
      const sort = 'id,desc'
      this.params = { page: this.page, size: this.size, sort: sort }
      return true
    },
    subDelete(id) {
      this.delLoading = true
      del(id).then(res => {
        this.delLoading = false
        this.$refs[id].doClose()
        this.dleChangePage()
        this.init()
        this.$notify({
          title: '删除成功',
          type: 'success',
          duration: 2500
        })
      }).catch(err => {
        this.delLoading = false
        this.$refs[id].doClose()
        console.log(err.response.data.message)
      })
    },
    add() {
      this.isAdd = true
      this.$refs.form.dialog = true
    },
    edit(data) {
      this.isAdd = false
      const _this = this.$refs.form
      _this.form = {
        id: data.id,
        productId: data.productId,
        title: data.title,
        image: data.image,
        unitName: data.unitName,
        stock: data.stock,
        sales: data.sales,
        images: data.images,
        imageArr: data.image.split(','),
        sliderImageArr: data.images.split(','),
        startTime: data.startTime,
        stopTime: data.stopTime,
        storeName: data.storeName,
        price: data.price,
        minPrice: data.minPrice,
        num: data.num,
        bargainMaxPrice: data.bargainMaxPrice,
        bargainMinPrice: data.bargainMinPrice,
        bargainNum: data.bargainNum,
        status: data.status,
        description: data.description,
        giveIntegral: data.giveIntegral,
        info: data.info,
        cost: data.cost,
        sort: data.sort,
        isHot: data.isHot,
        isDel: data.isDel,
        addTime: data.addTime,
        isPostage: data.isPostage,
        postage: data.postage,
        rule: data.rule,
        look: data.look,
        share: data.share,
        startTimeDate: new Date(data.startTimeDate),
        endTimeDate: new Date(data.endTimeDate)
      }
      _this.dialog = true
    }
  }
}
