import { add, edit } from '@/api/yxStoreBargain'
import editor from '../../components/Editor'
import MaterialList from '@/components/material'
export default {
  components: { editor, MaterialList },
  props: {
    isAdd: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      loading: false, dialog: false,
      form: {
        id: '',
        productId: '',
        title: '',
        image: '',
        unitName: '',
        stock: '',
        sales: '',
        images: '',
        imageArr: [],
        sliderImageArr: [],
        startTime: '',
        stopTime: '',
        storeName: '',
        price: '',
        minPrice: '',
        num: '',
        bargainMaxPrice: '',
        bargainMinPrice: '',
        bargainNum: '',
        status: '',
        description: '',
        giveIntegral: '',
        info: '',
        cost: '',
        sort: 0,
        isHot: 0,
        isDel: 0,
        addTime: '',
        isPostage: 1,
        postage: '',
        rule: '',
        look: '',
        share: '',
        startTimeDate: '',
        endTimeDate: ''
      },
      rules: {
      }
    }
  },
  watch: {
    'form.imageArr': function(val) {
      if (val) {
        this.form.image = val.join(',')
      }
    },
    'form.sliderImageArr': function(val) {
      if (val) {
        this.form.images = val.join(',')
      }
    }
  },
  methods: {
    cancel() {
      this.resetForm()
    },
    doSubmit() {
      this.loading = true
      if (this.isAdd) {
        this.doAdd()
      } else this.doEdit()
    },
    doAdd() {
      add(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '添加成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    doEdit() {
      edit(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '修改成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    resetForm() {
      this.dialog = false
      this.$refs['form'].resetFields()
      this.form = {
        id: '',
        productId: '',
        title: '',
        image: '',
        unitName: '',
        stock: '',
        sales: '',
        images: '',
        imageArr: [],
        sliderImageArr: [],
        startTime: '',
        stopTime: '',
        storeName: '',
        price: '',
        minPrice: '',
        num: '',
        bargainMaxPrice: '',
        bargainMinPrice: '',
        bargainNum: '',
        status: '',
        description: '',
        giveIntegral: '',
        info: '',
        cost: '',
        sort: '',
        isHot: '',
        isDel: '',
        addTime: '',
        isPostage: '',
        postage: '',
        rule: '',
        look: '',
        share: ''
      }
    }
  }
}
