import { add, edit } from '@/api/yxStoreCombination'
import editor from '../../components/Editor'
import MaterialList from '@/components/material'
export default {
  components: { editor,  MaterialList },
  props: {
    isAdd: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      loading: false, dialog: false,
      form: {
        id: '',
        productId: '',
        merId: '',
        image: '',
        images: '',
        imageArr: [],
        sliderImageArr: [],
        title: '',
        attr: '',
        people: '',
        info: '',
        price: '',
        sort: '',
        sales: '',
        stock: '',
        addTime: '',
        isHost: '',
        isShow: '',
        isDel: 0,
        merUse: '',
        isPostage: '',
        postage: '',
        description: '',
        startTime: '',
        stopTime: '',
        effectiveTime: '',
        cost: '',
        browse: '',
        unitName: '',
        combination: 1,
        browse: 0,
        startTimeDate: '',
        endTimeDate: ''
      },
      rules: {
      }
    }
  },
  watch: {
    'form.imageArr': function(val) {
      if (val) {
        this.form.image = val.join(',')
      }
    },
    'form.sliderImageArr': function(val) {
      if (val) {
        this.form.images = val.join(',')
      }
    }
  },
  methods: {
    cancel() {
      this.resetForm()
    },
    doSubmit() {
      this.loading = true
      if (this.isAdd) {
        this.doAdd()
      } else this.doEdit()
    },
    doAdd() {
      add(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '添加成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    doEdit() {
      edit(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '操作成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    resetForm() {
      this.dialog = false
      this.$refs['form'].resetFields()
      this.form = {
        id: '',
        productId: '',
        merId: '',
        image: '',
        images: '',
        imageArr: [],
        sliderImageArr: [],
        title: '',
        attr: '',
        people: 0,
        info: '',
        price: 0,
        sort: '',
        sales: '',
        stock: '',
        addTime: '',
        isHost: '',
        isShow: '',
        isDel: '',
        combination: '',
        merUse: '',
        isPostage: '',
        postage: '',
        description: '',
        startTimeDate: '',
        endTimeDate: '',
        effectiveTime: 24,
        cost: '',
        browse: '',
        unitName: ''
      }
    }
  }
}
