import { add, edit, grant } from '@/api/bopUser'
import { summary } from '@/api/bopVipPrice'

export default {
  props: {
    isAdd: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      loading: false, dialog: false,
      form: {
        uid: '',
        nickname: '',
        money: '',
        ptype: '1'
      },
      rules: {
      },
      vipOptions: []
    }
  },
  created() {
    this.getVipSummary();
  },
  methods: {
    getVipSummary() {
      summary().then(data => {
        console.log(data);
        this.vipOptions = data;
      })
    },
    cancel() {
      this.resetForm()
    },
    doSubmit() {
      this.loading = true
      grant(this.form).then(res => {
        this.resetForm()
        this.$notify({
          title: '修改成功',
          type: 'success',
          duration: 2500
        })
        this.loading = false
        this.$parent.init()
      }).catch(err => {
        this.loading = false
        console.log(err.response.data.message)
      })
    },
    resetForm() {
      this.dialog = false
      this.$refs['form'].resetFields()
      this.form = {
        uid: '',
        username: '',
        password: '',
        realName: '',
        birthday: '',
        cardId: '',
        mark: '',
        nickname: '',
        avatar: '',
        phone: '',
        email: '',
        city: '',
        company: '',
        position: '',
        concern: '',
        addTime: '',
        addIp: '',
        lastTime: '',
        lastIp: '',
        status: '',
        level: '',
        vipValidDate: '',
        isForever: '',
        userType: '',
        loginType: ''
      }
    }
  }
}
