import checkPermission from '@/utils/permission'
import initData from '@/mixins/crud'
import { del } from '@/api/yxSystemGroupData'
import eForm from './menuform'
export default {
  components: { eForm },
  mixins: [initData],
  data() {
    return {
      delLoading: false
    }
  },
  created() {
    this.$nextTick(() => {
      this.init()
    })
  },
  methods: {
    checkPermission,
    beforeInit() {
      this.url = 'api/yxSystemGroupData'
      const sort = 'id,desc'
      this.params = { page: this.page, size: this.size, sort: sort, groupName: 'yshop_home_menus' }
      return true
    },
    subDelete(id) {
      this.delLoading = true
      del(id).then(res => {
        this.delLoading = false
        this.$refs[id].doClose()
        this.dleChangePage()
        this.init()
        this.$notify({
          title: '删除成功',
          type: 'success',
          duration: 2500
        })
      }).catch(err => {
        this.delLoading = false
        this.$refs[id].doClose()
        console.log(err.response.data.message)
      })
    },
    add() {
      this.isAdd = true
      this.$refs.form.dialog = true
    },
    edit(data) {
      this.isAdd = false
      const _this = this.$refs.form
      _this.form = {
        id: data.id,
        groupName: data.groupName,
        name: data.map.name,
        url: data.map.url,
        wxapp_url: data.map.wxapp_url,
        uniapp_url: data.map.uniapp_url,
        pic: data.map.pic,
        imageArr: data.map.pic ? data.map.pic.split(',') : [],
        sort: data.sort,
        status: data.status
      }
      _this.dialog = true
    }
  }
}
